import React from "react";

function Recipe({ recipe, index, deleteRecipe }) {
  const content =
    recipe.type === "Text" ? (
      <p>{recipe.content}</p>
    ) : (
      <img href={recipe.content} />
    );
  const removeRecipe = () => {
    deleteRecipe(index);
  };

  return (
    <div className="recipe">
      {content}
      <button name="delete" onClick={deleteRecipe}>
        Delete
      </button>
    </div>
  );
}

export default Recipe;
